mapper.select:
SELECT *
FROM cart_item
WHERE cart_id = #{cart_id} AND ino = #{ino}

SELECT cart_item_id
FROM cart_item
WHERE cart_id = #{cart_id} AND ino = #{ino};

mapper.update:
UPDATE cart_item
SET count = count + #{count}
WHERE cart_id = #{cart_id} AND ino = #{ino};

UPDATE cart_item
SET count = #{count}
WHERE cart_id = #{cart_id} AND ino = #{ino};
